import styled from 'styled-components';
import { Drawer } from '@material-ui/core';

export const StyledDrawer = styled(Drawer)`
  > div:nth-child(2) {
    width: 120px!important;
    background: ${({ theme }) => theme.primary_dark}
  }

  height: 100%;
`;

export const Wrapper = styled.div`
  width: 100%!important;
  height: 100%;


  div, span, svg{
    color: ${({ theme }) => theme.primary_icon_color};
  }

  a.active > li{
    background: ${({ theme }) => theme.primary_selected_text_color};
    font-weight: bold;

    &:hover{
      background: ${({ theme }) => theme.primary_selected_text_color};
    }

    * {
      color: ${({ theme }) => theme.secondary_background_color};
    }
  }

  .selected span{
    color: ${({ theme }) => theme.primary_background_color};
    font-weight: bold;
  }

  div{
    flex-grow: 0;    
  }

  .logo{
    border-right: none;
    display: flex;
    justify-content: center;
    height: 60px;
    background: ${({ theme }) => theme.primary_dark}
  }

  a{
    text-decoration: none!important;

    li{
      padding: 0;
      font-size: $font-size-medium;
      text-transform: uppercase;
      height: 100px;
      display: flex;
      flex-direction: column;
      display: flex;
      align-items: center;
      justify-content: center;

      &:hover{
        background: inherit;
      }

      img{
        max-width: 50px;
      }
    }    
  }
`;

export const Logo = styled.div`
  border-right: none;
  display: flex;
  justify-content: center;
  height: 1000px;
`;

export const WrapperRtl = styled(Wrapper)`
  direction: rtl;
  text-align: right;
  justify-content: flex-start;
`;
